    }
  }
  else
  {
    item += "ADD " + (%incrementDirection == "Down" ? "-" : "") + %sender.ToString() + " to ";
    foreach (var receiver in %displayableReceivers)
      item += receiver + ", ";
  }
  item = item.Remove(item.Length - 2);
}
